// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library ValidatorLibrary {

    struct ValidatorData {
        address[] validators;
        mapping(address => bool) isValidator;
    }

    function numberOfValidators(ValidatorData storage self) internal view returns (uint) {
        return self.validators.length;
    }

    function isRegistered(ValidatorData storage self, address validator) internal view returns (bool) {
        return self.isValidator[validator];
    }

    function isNewsConfirmed(
        address[] memory validators,
        address[] memory confirmedValidators,
        uint minValidations
    ) internal pure returns (bool) {
        uint validationCount = 0;
        for (uint i = 0; i < validators.length; i++) {
            if (addressInArray(confirmedValidators, validators[i])) {
                validationCount++;
            }
        }
        return validationCount >= minValidations;
    }

    function addressInArray(address[] memory array, address value) internal pure returns (bool) {
        for (uint i = 0; i < array.length; i++) {
            if (array[i] == value) {
                return true;
            }
        }
        return false;
    }
}
